        {
          all_product.map((item, index)=>{

            if (props.category===item.category) {
              
              return <Item key={index} id={item.id} title={item.title} image={item.image} price={item.price} description={item.description} /> 
              
            }
            
            else{
              return null;
            }
        
            
          })}


        <h1>{totalProducts}</h1> 
          
      </div>
      
      </div>
      

  )
}

export default ShopCategory
