import React, {createContext, useEffect, useState, useParams} from "react";



export const ShopContext = createContext(null);

const getDefaultCart = ()=>{
    let cart = {};
    for (let index=0; index < 300+1; index++){
        cart[index] = 0;
    }
    return cart;
}

const ShopContextProvider = (props)=>{

    const [all_product, setAll_Product] = useState([]);
    const [cartItems, setCartItems] = useState(getDefaultCart());
    const [menu] = useParams();

    useEffect (()=>{
        fetch("http://localhost:4000/allproducts")
        .then((res)=>res.json())
        .then((data)=>setAll_Product(data))

        if(localStorage.getItem("auth-token")){
            fetch("http://localhost:4000/getcart", {
                method:"POST",
                headers:{
                    Accept:"application/form-data",
                    "auth-token":`${localStorage.getItem("auth-token")}`,
                    "Content-Type":"application/json",
                },
                body:"",
            }).then((res)=>res.json())
            .then((data)=>setCartItems(data));
        }
    },[])

    const addToCart = (itemId)=>{
        setCartItems((prev)=>({...prev, [itemId]:prev[itemId] +1}));
        if(localStorage.getItem("auth-token")){
            fetch("http://localhost:4000/addtocart",{
                method:"POST",
                headers:{
                    Accept:"application/form-data",
                    "auth-token":`${localStorage.getItem("auth-token")}`, 
                    "Content-Type":"application/json",
                },
                body:JSON.stringify({"itemId":itemId}),
            })
            .then((res)=>res.json())
            .then((data)=>console.log(data));
        }
    }

    const removeFromCart = (itemId)=>{
        setCartItems((prev)=>({...prev,[itemId]:prev[itemId]-1}));
        if(localStorage.getItem("auth-token")){
            fetch("http://localhost:4000/removefromcart",{
                method:"POST",
                headers:{
                    Accept:"application/form-data",
                    "auth-token":`${localStorage.getItem("auth-token")}`, 
                    "Content-Type":"application/json",
                },
                body:JSON.stringify({"itemId":itemId}),
            })
            .then((res)=>res.json())
            .then((data)=>console.log(data));
        }
    };

    const getTotalCartAmount = ()=>{
        let totalAmount=0;
        for(const item in cartItems)
        {
            if(cartItems[item]>0)
            {
                let itemInfo = all_product.find((product)=>product.id===Number(item));
                totalAmount+= itemInfo.price*cartItems[item];
            }
        }
        return totalAmount;
    };

    const getTotalCartItems = ()=>{
        let totalItems = 0;
        for(const item in cartItems)
        {
            if(cartItems[item]>0)
            {
                totalItems += cartItems[item];
            }
        }
        return totalItems;
    };

    const getTotalProducts = (menu)=>{
        let totalProducts = 0;
    
        for(const item in all_product)
        {
            if(item.category===menu){totalProducts += 1;}
            }
        return totalProducts;
    };

    

    const contextValue = {getTotalProducts, getTotalCartItems, all_product, cartItems, addToCart, removeFromCart, getTotalCartAmount};

    

    return (
        <ShopContext.Provider value={contextValue}>
            {props.children}
        </ShopContext.Provider>
    )
}

export default ShopContextProvider;
